pragma solidity 0.4.24;

import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "erc721x/contracts/Interfaces/ERC721X.sol";

contract CardFaucet {

    event GeneratedCard(uint256 cardId, BoosterType boosterType);
    event UpgradedCardToBE(uint256 cardId);
    event UpgradedCardToLE(uint256 cardId);
    event LimitedDifficultyChanged(uint256 target, uint256 total);
    event BackerDifficultyChanged(uint256 target, uint256 total);
    event DropratesSet(uint256 boosterTypes, uint256 rarity, uint256[] cardList);

    bool inBackersPeriod = true; // BE cards only claimable through the faucet as long as this is true
    // We want 15% of all cards to be BE. Since a card that may be upgraded is only 1/5 per booster, it must have 5 * 15% chance to be BE.
    uint256 private BE_TARGET = 750;
    // We want 0.2% of all cards to be LE. Since a card that may be upgraded is only 1/5 per booster, it must have 10% chance to be BE.
    uint256 private LE_TARGET = 10;
    uint256 private TOTAL = 1000;

    ERC721X zbgCard;
    ERC20 boosterPack;
    ERC20 superPack;
    ERC20 airPack;
    ERC20 earthPack;
    ERC20 firePack;
    ERC20 lifePack;
    ERC20 toxicPack;
    ERC20 waterPack;

    enum BoosterType {
        BOOSTER,
        SUPER,
        AIR,
        EARTH,
        FIRE,
        LIFE,
        TOXIC,
        WATER
    }

    mapping(address => bool) validators;

    // General  : [0, 15)   - 1.5% chance
    // Commander: [15, 75)  - 6% chance
    // Officer  : [75, 325) - 25% chance
    // Minion   : 325, 1000 - 67.5% chance
    uint256[] dropRateKeys = [15, 75, 325, 1000];
    uint256 constant COMMON_ROLL = 1000;
    uint256 constant RARE_ROLL = 325; // Roll up to 325 to get a guaranteed rare
    uint256 constant RARITIES = 10;
    uint256 nonce;
    mapping(uint256 => mapping(uint256 => uint256[])) dropRateValues;

    constructor(
        address creator,
        address cardAddress,
        address boosterPackAddr,
        address superPackAddr,
        address airPackAddr,
        address earthPackAddr,
        address firePackAddr,
        address lifePackAddr,
        address toxicPackAddr,
        address waterPackAddr
    ) public {
        validators[creator] = true;
        zbgCard = ERC721X(cardAddress);
        boosterPack = ERC20(boosterPackAddr);
        superPack = ERC20(superPackAddr);
        airPack = ERC20(airPackAddr);
        earthPack = ERC20(earthPackAddr);
        firePack = ERC20(firePackAddr);
        lifePack = ERC20(lifePackAddr);
        toxicPack = ERC20(toxicPackAddr);
        waterPack = ERC20(waterPackAddr);
    }

    function setupDroprates(uint256 boosterType, uint256 rarity, uint256[] cardList) public onlyValidator {
        dropRateValues[boosterType][rarity] = cardList;
        emit DropratesSet(boosterType, rarity, cardList);
    }

    function getDroprates(uint256 boosterType, uint256 rarity) public view returns (uint256[] cardList) {
        cardList = dropRateValues[boosterType][rarity];
    }

    function openBoosterPack(BoosterType boosterType) external {
        ERC20 booster = getBoosterPack(boosterType);

        // Get the booster pack address
        booster.transferFrom(msg.sender, address(this), 1);

        // If the boosterpack is a super pack, we give 4 normal and 1 rare from
        // the starter pack
        for (uint256 i = 0; i < 4; i++) {
            zbgCard.safeTransferFrom(address(zbgCard), msg.sender, _generateCard(boosterType), 1);
        }

        // Check if the 5th card should be upgraded.
        // If so, instead of sending the usual tokenId,
        // send `tokenId+1` which is the BE version
        uint256 tokenId = _generateCard(boosterType); // Super packs give a guaranteed rare
        tokenId = shouldUpgrade(tokenId);

        zbgCard.safeTransferFrom(address(zbgCard), msg.sender, tokenId, 1);
    }

    /// @notice Upgrade only if the random roll from (0, `TOTAL`) is less than `target`
    /// @dev Call `updateBackerDifficulty` from a validator account in order
    ///      to update the target values (ideally with values calculated
    ///      from an algorithm that takes elo and previous difficulty into account)
    function shouldUpgrade(uint256 tokenId) private returns (uint256) {
        uint256 rand = _randomInRange(0, TOTAL);
        if  (rand < LE_TARGET) {
            tokenId += 2;
            emit UpgradedCardToLE(tokenId);
        }
        else if (rand < BE_TARGET && inBackersPeriod) {
            tokenId += 1;
            emit UpgradedCardToBE(tokenId);
        }
        return tokenId;
    }

    function _generateCard(BoosterType boosterType) private returns (uint256 cardId) {
        // Rolls bigger than RARE_ROLL are commons. We exclude these
        // in the case of a rare roll
        uint256 roll = boosterType == BoosterType.SUPER ? RARE_ROLL : COMMON_ROLL ;
        uint256 _randomNumber = _randomInRange(0, roll);
        uint256 level = getDropRateLevel(_randomNumber);

        // if the booster is a super one we get
        // the drop rate values from the starter pack
        if (boosterType == BoosterType.SUPER) {
            boosterType = BoosterType.BOOSTER;
        }

        // List of cards at that rarity
        uint256[] memory cards = dropRateValues[uint256(boosterType)][level];
        require(cards.length > 0, "found an empty cards array");

        // Pick a random cardId in that range
        uint256 index = _randomInRange(0, cards.length);
        cardId = RARITIES * cards[index]; // Return cardId * RARITIES to match the token indexing for all rarities.
        emit GeneratedCard(cardId, boosterType);
    }

    function getDropRateLevel(uint256 num) private view returns (uint256 level) {
        uint256 length = dropRateKeys.length;
        require(num >= 0 && num <= 1000, "Drop rate number out of bounds");
        for (uint i =  0; i < length ; i++ ) {
            if (num <= dropRateKeys[i]) {
                level = dropRateKeys[i];
                break;
            }
        }
    }
    function getBoosterPack(BoosterType boosterType) private view returns (ERC20 booster) {
        if (boosterType == BoosterType.BOOSTER) {
            booster = boosterPack;
        } else if (boosterType == BoosterType.SUPER) {
            booster = superPack;
        } else if (boosterType == BoosterType.AIR) {
            booster = airPack;
        } else if (boosterType == BoosterType.EARTH) {
            booster = earthPack;
        } else if (boosterType == BoosterType.FIRE) {
            booster = firePack;
        } else if (boosterType == BoosterType.LIFE) {
            booster = lifePack;
        } else if (boosterType == BoosterType.TOXIC) {
            booster = toxicPack;
        } else if (boosterType == BoosterType.WATER) {
            booster = waterPack;
        } else {
            revert("Wrong booster type");
        }
    }

    function _randomInRange(uint256 min, uint256 max) private returns (uint256) {
        uint256 rand = uint256(keccak256(abi.encodePacked(blockhash(block.number-1), nonce)));
        nonce++;
        return min + rand % (max-min);
    }

    function updateDifficultyBE(uint256 _target, uint256 _total) public onlyValidator {
        BE_TARGET = _target;
        TOTAL = _total;
        emit BackerDifficultyChanged(BE_TARGET, _total);
    }

    function updateDifficultyLE(uint256 _target, uint256 _total) public onlyValidator {
        LE_TARGET = _target;
        TOTAL = _total;
        emit LimitedDifficultyChanged(LE_TARGET, _total);
    }

    // Function to trigger BE cards being generated.
    function endBackersPeriod() external onlyValidator {
        inBackersPeriod = false;
    }

    function addValidator(address newValidator) external onlyValidator {
        validators[newValidator] = true;
    }

    modifier onlyValidator() {
        require(validators[msg.sender] == true,  'Validator only');
        _;
    }
}

