pragma solidity 0.4.24;

import {MintableToken as ERC20} from "openzeppelin-solidity/contracts/token/ERC20/MintableToken.sol";
import "openzeppelin-solidity/contracts/ownership/Ownable.sol";


contract FiatPurchase is Ownable {
    uint256 contractVersion = 1;

    address originOwner;
    address gatewayAddr;

    bool isContractEnabled = true;

    mapping(uint256 => uint256) public nonces; //map transactionID to nounce check

    event PurchaseSent(address sender, uint256 txId, uint256 userId, bytes32 hash, address ecrecover, address gatewayAddr);

    ERC20 boosterPack;
    ERC20 superPack;
    ERC20 airPack;
    ERC20 earthPack;
    ERC20 firePack;
    ERC20 lifePack;
    ERC20 toxicPack;
    ERC20 waterPack;

    constructor (
        address deployerAddr,
        address gatewayKeyPubAddr,
        address boosterPackAddr,
        address superPackAddr,
        address airPackAddr,
        address earthPackAddr,
        address firePackAddr,
        address lifePackAddr,
        address toxicPackAddr,
        address waterPackAddr
    ) public {
        require(gatewayKeyPubAddr != address(0x0));
        gatewayAddr = gatewayKeyPubAddr;
        boosterPack = ERC20(boosterPackAddr);
        superPack = ERC20(superPackAddr);
        airPack = ERC20(airPackAddr);
        earthPack = ERC20(earthPackAddr);
        firePack = ERC20(firePackAddr);
        lifePack = ERC20(lifePackAddr);
        toxicPack = ERC20(toxicPackAddr);
        waterPack = ERC20(waterPackAddr);
        Ownable(deployerAddr);
    }

    function requestPacks(
        uint256 userID,
        bytes32 r,
        bytes32 s,
        uint8 v,
        bytes32 hash,
        uint256[8] amounts,
        uint256 txID
    ) public {
        require(keccak256(abi.encodePacked(
            userID,
            amounts[0],
            amounts[1],
            amounts[2],
            amounts[3],
            amounts[4],
            amounts[5],
            amounts[6],
            amounts[7],
            txID,
            contractVersion)) == hash, "Hash mismatch");        

        require(nonces[txID] < 1, "Transaction claimed");
        require(ecrecover(hash, v, r, s) == gatewayAddr, "Invalid signature");
        require(isContractEnabled, "Contract terminated");

        boosterPack.mint(msg.sender, amounts[0]);
        superPack.mint(msg.sender, amounts[1]);
        airPack.mint(msg.sender, amounts[2]);
        earthPack.mint(msg.sender, amounts[3]);
        firePack.mint(msg.sender, amounts[4]);
        lifePack.mint(msg.sender, amounts[5]);
        toxicPack.mint(msg.sender, amounts[6]);
        waterPack.mint(msg.sender, amounts[7]);

        nonces[txID] = 1;

        emit PurchaseSent(msg.sender, txID, userID, hash, ecrecover(hash, v, r, s), gatewayAddr);

    }

    function stopContract() onlyOwner public  {
        isContractEnabled = false;
    }
}
