pragma solidity 0.4.24;

import {MintableToken as ERC20} from "openzeppelin-solidity/contracts/token/ERC20/MintableToken.sol";
import "openzeppelin-solidity/contracts/ownership/Ownable.sol";


contract PackSale is Ownable {

    event PurchaseSent(address customer,uint256[8] amounts, uint256 totalPrice);

    address deployer;

    ERC20 boosterPack;
    ERC20 superPack;
    ERC20 airPack;
    ERC20 earthPack;
    ERC20 firePack;
    ERC20 lifePack;
    ERC20 toxicPack;
    ERC20 waterPack;

    // TODO: recheck unit
    uint256[8] defaultPackPrice = [0.1 ether, 0.3 ether, 0.2 ether, 0.2 ether, 0.2 ether, 0.2 ether, 0.2 ether, 0.2 ether];

    // pack type => price
    mapping(uint256 => uint256) prices;

    constructor (
        address creator,
        address boosterPackAddr,
        address superPackAddr,
        address airPackAddr,
        address earthPackAddr,
        address firePackAddr,
        address lifePackAddr,
        address toxicPackAddr,
        address waterPackAddr
    ) public {
        boosterPack = ERC20(boosterPackAddr);
        superPack = ERC20(superPackAddr);
        airPack = ERC20(airPackAddr);
        earthPack = ERC20(earthPackAddr);
        firePack = ERC20(firePackAddr);
        lifePack = ERC20(lifePackAddr);
        toxicPack = ERC20(toxicPackAddr);
        waterPack = ERC20(waterPackAddr);
        Ownable(creator);

        // set default packs price
        for (uint8 packType = 0; packType < 8; packType++) {
            prices[packType] = defaultPackPrice[packType];
        }

    }

    function setPackPrice(uint256 _type, uint256 _newPrice) external onlyOwner {
        require(_type >= 0 && _type <= 7, "Pack type doesn't exist");
        prices[_type] = _newPrice;
    }

    function purchasePacks(uint256[8] amounts) external payable {
        require(amounts.length == 8, "Invalid amounts value");
        uint256 packPrice = calculatePackPrice(amounts);
        require(msg.value >= packPrice, "Insufficient eth amount");
        boosterPack.mint(msg.sender, amounts[0]);
        superPack.mint(msg.sender, amounts[1]);
        airPack.mint(msg.sender, amounts[2]);
        earthPack.mint(msg.sender, amounts[3]);
        firePack.mint(msg.sender, amounts[4]);
        lifePack.mint(msg.sender, amounts[5]);
        toxicPack.mint(msg.sender, amounts[6]);
        waterPack.mint(msg.sender, amounts[7]);
        emit PurchaseSent(msg.sender, amounts, packPrice);
    }

    function calculatePackPrice(uint256[8] _amounts) private returns (uint256 total){
        total = 0;
        for (var packType = 0; packType < _amounts.length; packType++) {
            total += _amounts[packType] * prices[packType];
        }
    }

    function getCurrentPrice() external returns (uint256[8] currentPrice) {
        for(uint256 index=0; index < 8; index++) {
            currentPrice[index] = prices[index];
        }
    }
}
